mapper.select:
select m.id, m.styleCode, m.styleId, m.employeeName, m.employeeId, m.processName, m.processPrice, m.finishNum, (m.processPrice * m.finishNum) as payMoney, m.createDate from ( SELECT a.id, (select c.id from jk_clothe_process b,jk_clothe_style c where b.styleId=c.id and b.id=a.processId) as styleId, (select c.styleCode from jk_clothe_process b,jk_clothe_style c where b.styleId=c.id and b.id=a.processId) as styleCode, (select t.name from jk_student t where t.id=a.employeeId) as employeeName, a.employeeId, (select b.processName from jk_clothe_process b where b.id=a.processId) as processName, (select b.processPrice from jk_clothe_process b where b.id=a.processId) as processPrice, a.finishNum, a.createDate FROM jk_process_finish a WHERE 1=1 and a.finishNum>0 ) m where 1=1
and m.styleCode like '%${styleCode}%'
and m.employeeId=#{employeeId}
and date_format(m.createDate,'%Y-%m-%d')>=#{startPayDate}
and date_format(m.createDate,'%Y-%m-%d')<=#{endPayDate}
order by m.createDate desc
limit #{rowNum},#{pageSize}

select count(m.id) from ( SELECT a.id, (select c.id from jk_clothe_process b,jk_clothe_style c where b.styleId=c.id and b.id=a.processId) as styleId, (select c.styleCode from jk_clothe_process b,jk_clothe_style c where b.styleId=c.id and b.id=a.processId) as styleCode, (select t.name from jk_student t where t.id=a.employeeId) as employeeName, a.employeeId, (select b.processName from jk_clothe_process b where b.id=a.processId) as processName, (select b.processPrice from jk_clothe_process b where b.id=a.processId) as processPrice, a.finishNum, a.createDate FROM jk_process_finish a WHERE 1=1 and a.finishNum>0 ) m where 1=1
and m.styleId=#{styleId}
and m.employeeId=#{employeeId}
and date_format(m.createDate,'%Y-%m-%d')>=#{startPayDate}
and date_format(m.createDate,'%Y-%m-%d')<=#{endPayDate}
